// final list will be provided by compiler
const _RESERVED = {
  WHERE: 1,
  GROUP: 1,
  ORDER: 1,
  BY: 1,
  AT: 1,
  NO: 1,
  LIMIT: 1
  // ...
}

const _slugify = name => name.replace(/\./g, '_')

module.exports = {
  quoted: name => `"${name}"`,
  plain: name => {
    const upper = name.toUpperCase()
    return upper in _RESERVED ? `"${upper}"` : _slugify(name)
  },
  'all-upper': name => `"${_slugify(name.toUpperCase())}"`,
  'all-lower': name => `"${_slugify(name.toLowerCase())}"`,
  bracketed: name => `[${name}]`
}
